import React, { useState, useEffect, useCallback } from 'react';
import { View, Text, TextInput, Button, StyleSheet, ScrollView, ActivityIndicator, Alert, TouchableOpacity, Image, Platform, Switch } from 'react-native';
import { useLocalSearchParams, useRouter } from 'expo-router';
import { WebView } from 'react-native-webview';
import { usePacking } from '../../../src/hooks/usePacking';
import { useShow } from '../../../src/hooks/useShow';
import { PackingBox } from '../../../src/types/packing';
import { Show } from '../../../src/types';
import * as Print from 'expo-print';
import QRCodeLib from 'qrcode';
import { useAuth } from '../../../src/contexts/AuthContext';
import { Address } from '../../../src/shared/types/address';
import { AddressSelectionModal } from '../../../src/shared/components/AddressSelectionModal';

// Import images using relative paths with .webp extension
import handleCareImage from '../../../assets/packing-symbols/handle-care.webp';
import fragileImage from '../../../assets/packing-symbols/fragile.webp';
import thisWayUpImage from '../../../assets/packing-symbols/this-way-up.webp';
import keepDryImage from '../../../assets/packing-symbols/keep-dry.webp';
import batteryIncluded from '../../../assets/packing-symbols/battery-included.webp';

// Base64 encoded image data URLs.
const HANDLE_CARE_BASE64 = 'data:image/webp;base64,';
const FRAGILE_BASE64 = 'data:image/webp;base64,';
const THIS_WAY_UP_BASE64 = 'data:image/webp;base64,';
const KEEP_DRY_BASE64 = 'data:image/webp;base64,';
const BATTERY_INCLUDED = 'data:image/webp;base64,';
// Define a default empty address
const defaultAddress: Address = {
  id: '',
  name: '',
  companyName: '',
  street1: '',
  street2: '',
  city: '',
  region: '',
  postalCode: '',
  country: 'United Kingdom', // Default to UK
  nickname: '',
};

export default function LabelPreviewPage() {
  const router = useRouter();
  const { id: boxId, showId } = useLocalSearchParams<{ id: string, showId?: string }>();
  const { userProfile, addSavedAddress } = useAuth();
  
  console.log(`[LabelPreviewPage] Received boxId: ${boxId}, showId: ${showId}`);

  const [handlingNote, setHandlingNote] = useState('');
  const [includeFragile, setIncludeFragile] = useState(false);
  const [includeThisWayUp, setIncludeThisWayUp] = useState(false);
  const [includeKeepDry, setIncludeKeepDry] = useState(false);
  const [includeBatteries, setIncludeBatteries] = useState(false);
  const [isPrinting, setIsPrinting] = useState(false);
  const [qrDataURL, setQrDataURL] = useState<string | null>(null);
  const [htmlContent, setHtmlContent] = useState<string | null>(null);
  const [isGeneratingPreview, setIsGeneratingPreview] = useState(true);
  const [isSavingNote, setIsSavingNote] = useState(false);

  // State for addresses
  const [senderAddress, setSenderAddress] = useState<Address>(defaultAddress);
  const [deliveryAddress, setDeliveryAddress] = useState<Address>(defaultAddress);
  
  // New state for modal management
  const [isAddressModalVisible, setIsAddressModalVisible] = useState(false);
  const [editingAddressType, setEditingAddressType] = useState<'sender' | 'delivery' | null>(null);

  // Get showId from context or route params if needed for usePacking
  const { boxes, loading: packingLoading, error: packingError, updateBoxLabelSettings, getDocument } = usePacking(showId);
  const [box, setBox] = useState<PackingBox | null>(null);
  const { show, loading: showLoading, error: showError } = useShow(showId);

  const hasFragileItems = box?.props?.some(p => p.isFragile) ?? false;
  // Calculate total weight
  const totalWeightKg = box?.props?.reduce((sum, p) => sum + (p.weight || 0), 0) ?? 0;
  const weightUnit = box?.weightUnit || 'kg'; // Use unit from box or default

  const origin = Platform.OS === 'web' ? window.location.origin : 'https://yourapp.com';
  const qrValue = boxId && showId ?
    `${origin}/(web)/packing/box/${boxId}?showId=${showId}` :
    'Error: Missing ID';

  useEffect(() => {
    if (!showId) {
        console.error("Label page requires showId param");
        return;
    }
    if (boxId && boxes.length > 0) {
      const foundBox = boxes.find(b => b.id === boxId);
      
      setBox(foundBox || null);
      if (foundBox) {
        const initialNote = foundBox.labelHandlingNote ?? '';
        const initialFragile = foundBox.labelIncludeFragile ?? foundBox.props?.some(p => p.isFragile) ?? false;
        const initialThisWayUp = foundBox.labelIncludeThisWayUp ?? false;
        const initialKeepDry = foundBox.labelIncludeKeepDry ?? false;
        const initialBatteries = foundBox.labelIncludeBatteries ?? false;

        setIncludeFragile(initialFragile);
        setIncludeThisWayUp(initialThisWayUp);
        setIncludeKeepDry(initialKeepDry);
        setIncludeBatteries(initialBatteries);
        setHandlingNote(initialNote);

        // Set initial addresses from box if available (assuming they are stored there)
        // This part might need adjustment based on how addresses are associated with boxes.
        // For now, we keep using defaultAddress or what's already in senderAddress/deliveryAddress state.
        // if (foundBox.senderAddress) setSenderAddress(foundBox.senderAddress);
        // if (foundBox.deliveryAddress) setDeliveryAddress(foundBox.deliveryAddress);

      }
    }
  }, [boxId, boxes, showId]);

  useEffect(() => {
    if (qrValue && qrValue !== 'Error: Missing ID') {
      const generateQr = async () => {
        try {
          const url = await QRCodeLib.toDataURL(qrValue, { width: 250, margin: 2 });
          setQrDataURL(url);
        } catch (qrError) {
          console.error("[LabelPreviewPage] Error generating QR code data URL:", qrError);
          setQrDataURL(null);
          Alert.alert('Error', 'Could not generate QR code for preview.');
        }
      };
      generateQr();
    } else {
      console.warn('[LabelPreviewPage] QR code value is missing or invalid, cannot generate QR.');
      setQrDataURL(null);
    }
  }, [qrValue]);

  useEffect(() => {
    if (box && qrDataURL && show) {
      const generatedHtml = `
        <html>
          <head>
            <meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0, user-scalable=no" />
            <style>
              @page {
                size: A5; /* Or your desired label size, e.g., 100mm 150mm; */
                margin: 5mm; 
              }
              body { font-family: Arial, sans-serif; margin: 0; padding: 0; background-color: #fff; color: #000; }
              .label-container { 
                width: 100%; 
                height: calc(100% - 10mm); 
                border: 2px solid black; 
                display: flex;
                flex-direction: column;
              }
              /* Removed table styles as we are using flexbox primarily */

              .section { border-bottom: 2px solid black; }
              .section:last-child { border-bottom: none; }

              .show-name-section { text-align: center; font-size: 16px; font-weight: bold; padding: 8px; }

              .top-content-section { display: flex; flex-direction: row; flex-grow: 0; /* Prevent this section from growing too much */ }
              .top-left-info { width: 30%; padding: 10px; border-right: 2px solid black; text-align: left; display: flex; flex-direction: column; justify-content: space-around; }
              .top-left-info .box-id-main { font-size: 24px; font-weight: bold; margin-bottom: 10px; line-height: 1.2;}
              .top-left-info .approx-weight-label { font-size: 12px; }
              .top-left-info .approx-weight-value { font-size: 16px; font-weight: bold; }
              
              .qr-and-symbols-area { /* Renamed from qr-code-area */
                width: 70%; /* Adjusted width */
                padding: 5px; 
                display: flex; 
                flex-direction: row; 
                align-items: center; /* Align items vertically */
                justify-content: space-around; /* Distribute space */
              }
              .qr-code-container { /* Container for just the QR */
                flex-grow: 1; 
                text-align: center; 
                padding: 5px;
              }
              .qr-code { max-width: 215px; height: auto; display: block; margin: 0 ; } /* Original size before SHOW CONTENTS */
              
              .symbols-column { 
                display: flex; 
                flex-direction: column; 
                justify-content: space-around; /* Distribute symbols vertically */
                align-items: center; 
                padding: 5px; /* Original padding */
                height: 100%; /* Original height setting */
              }
              .symbol-image { width: 50px; height: 50px; margin: 2px 0; border: 1px solid #ccc; padding: 2px; box-sizing: border-box;} /* Original symbol size */

              .box-details-section { padding: 10px; }
              .box-name-main { font-size: 18px; font-weight: bold; margin-bottom: 5px; }
              .handling-notes-label { font-size: 12px; font-weight: bold; }
              .handling-notes-content { font-size: 12px; }

              .address-section { display: flex; flex-direction: row; flex-grow: 1; }
              .delivery-address-area { width: 50%; padding: 10px; border-right: 1px solid black; }
              .sender-address-area { width: 50%; padding: 10px; }
              
              .address-title { font-size: 18px; font-weight: bold; margin-bottom: 8px; }
              .address-details p { margin: 1px 0; font-size: 10px; line-height: 1.2; }
              .address-details strong { font-size: 10px; }
            </style>
          </head>
          <body>
            <div class="label-container">
              <!-- Show Name Section -->
              <div class="section show-name-section">
                    ${show.name || 'Show Name Unavailable'}
              </div>

              <!-- Top Content: Box Info & QR Code with Symbols -->
              <div class="section top-content-section">
                <div class="top-left-info">
                  <div class="box-id-main">Box ${box?.sceneNumber || box?.id || 'N/A'} of ${boxes.length > 0 ? boxes.length : 'N/A'}</div>
                  <div>
                    <div class="approx-weight-label">Approx Weight:</div>
                    <div class="approx-weight-value">${totalWeightKg.toFixed(1)} ${weightUnit}</div>
                  </div>
                </div>
                <div class="qr-and-symbols-area">
                  <div class="qr-code-container">
                      <img src="${qrDataURL}" alt="QR Code" class="qr-code" />
                     <p>Scan to view Box contents</p>
                  </div>
                  <div class="symbols-column">
                    ${ (hasFragileItems || includeFragile || includeThisWayUp || includeKeepDry || includeBatteries) ? `<img src="${HANDLE_CARE_BASE64}" alt="Handle With Care" class="symbol-image" />` : ''}
                        ${includeFragile ? `<img src="${FRAGILE_BASE64}" alt="Fragile" class="symbol-image" />` : ''}
                        ${includeThisWayUp ? `<img src="${THIS_WAY_UP_BASE64}" alt="This Way Up" class="symbol-image" />` : ''}
                        ${includeKeepDry ? `<img src="${KEEP_DRY_BASE64}" alt="Keep Dry" class="symbol-image" />` : ''}
                    ${includeBatteries ? `<img src="${BATTERY_INCLUDED}" alt="Includes Batteries" class="symbol-image" />` : ''}
                      </div>
                    </div>
                       </div>
                      
              <!-- Box Name and Handling Notes Section -->
              <div class="section box-details-section">
                <div class="box-name-main">${box.name || 'Box Name N/A'}</div>
                <div class="handling-notes-label">Handling Notes:</div>
                <div class="handling-notes-content">${handlingNote || 'None'}</div>
                    </div>

              <!-- Addresses Section -->
              <div class="section address-section">
                <div class="delivery-address-area">
                  <div class="address-title">DELIVERY:</div>
                            <div class="address-details">
                    <p><strong>${deliveryAddress.name || ''}</strong></p>
                    <p>${deliveryAddress.companyName || ''}</p>
                    <p>${deliveryAddress.street1 || ''}</p>
                    ${deliveryAddress.street2 ? `<p>${deliveryAddress.street2}</p>` : ''}
                    <p>${deliveryAddress.city || ''}</p>
                    <p>${deliveryAddress.region || ''}</p>
                    <p>${deliveryAddress.postalCode || ''}</p>
                    <p>${deliveryAddress.country || ''}</p>
                            </div>
                          </div>
                <div class="sender-address-area">
                  <div class="address-title">SENDER:</div>
                            <div class="address-details">
                    <p><strong>${senderAddress.name || ''}</strong></p>
                    <p>${senderAddress.companyName || ''}</p>
                    <p>${senderAddress.street1 || ''}</p>
                    ${senderAddress.street2 ? `<p>${senderAddress.street2}</p>` : ''}
                    <p>${senderAddress.city || ''}</p>
                    <p>${senderAddress.region || ''}</p>
                    <p>${senderAddress.postalCode || ''}</p>
                    <p>${senderAddress.country || ''}</p>
                            </div>
                          </div>
              </div>
            </div>
          </body>
        </html>
      `;
      setHtmlContent(generatedHtml);
      setIsGeneratingPreview(false); // Successfully generated
    } else {
      setHtmlContent(null);
      // Adjust isGeneratingPreview logic - depends only on packingLoading now
      const doneLoading = !packingLoading && !showLoading;
      const missingDeps = !box || !qrDataURL || !show;
      setIsGeneratingPreview(doneLoading && missingDeps);
    }
  }, [box, qrDataURL, show, handlingNote, includeFragile, includeThisWayUp, includeKeepDry, includeBatteries, hasFragileItems, packingLoading, showLoading, boxes.length, senderAddress, deliveryAddress, weightUnit, totalWeightKg]); // Added weightUnit, totalWeightKg

  // Debounced save function (to be implemented within usePacking ideally)
  const handleSettingChange = useCallback(async (settings: Partial<PackingBox>) => {
      // Check if both boxId (from params) and box (from state) are available AND match
      if (!boxId || !box || box.id !== boxId) { 
        console.warn(`[LabelPreviewPage] Attempted to save settings but box state is not ready or mismatched. Aborting. boxId=${boxId}, box?.id=${box?.id}`);
        return;
      }
      
      // Diagnostic: Explicitly check if Firestore can find the doc right now
      const docExists = await getDocument(boxId);
      if (!docExists) {
        console.error(`[LabelPreviewPage] Pre-update check FAILED: getDocument could not find ${boxId}. Aborting update.`);
        Alert.alert('Save Error', 'Could not find the box data to save to. Please try again later.');
        return;
      }

      console.log(`[LabelPreviewPage] Saving settings for box ${boxId}:`, settings);
      
      try {
        await updateBoxLabelSettings(boxId, settings);
      } catch (error) {
        console.error('[LabelPreviewPage] Error saving settings:', error);
        Alert.alert('Save Error', 'Could not save handling note. Please try again.');
      } 
  }, [boxId, box, updateBoxLabelSettings, getDocument]);

  // Update handleHandlingNoteChange to ONLY update local state
  const handleHandlingNoteChange = (text: string) => {
      setHandlingNote(text); // Update local state immediately
  };

  // ADD function to handle saving on blur
  const handleHandlingNoteBlur = async () => {
    console.log('[LabelPreviewPage] Handling Note blurred. Current value:', handlingNote);
    // Optionally check if the note actually changed from the initial state if needed
    
    setIsSavingNote(true); // Start saving indicator
    try {
      await handleSettingChange({ labelHandlingNote: handlingNote });
    } finally {
      setIsSavingNote(false); // Stop saving indicator regardless of outcome
    }
  };

  const handleFragileChange = (value: boolean) => {
      setIncludeFragile(value);
      handleSettingChange({ labelIncludeFragile: value });
  }

  const handleThisWayUpChange = (value: boolean) => {
      setIncludeThisWayUp(value);
      handleSettingChange({ labelIncludeThisWayUp: value });
  }

  const handleKeepDryChange = (value: boolean) => {
      setIncludeKeepDry(value);
      handleSettingChange({ labelIncludeKeepDry: value });
  }

  const handleBatteriesChange = (value: boolean) => {
    setIncludeBatteries(value);
    handleSettingChange({ labelIncludeBatteries: value });
  }
  const handlePrint = async () => {
    if (!htmlContent) {
        Alert.alert('Error', 'Label preview is not ready. Please wait.');
        return;
    }

    // Saving new addresses from this page is now obsolete as it moves to the modal.
    // The modal will need its own save logic.
    // For now, removing the direct save attempts from here.
    // try {
    //   if (showNewSenderAddressForm && saveSenderAddress && senderAddress.name && senderAddress.street1 && senderAddress.postalCode && senderAddress.country) {
    //     console.log("[LabelPreviewPage] Attempting to save new sender address:", senderAddress);
    //     const { id, ...newAddressData } = senderAddress; // Exclude ID for new address
    //     await addSavedAddress('sender', newAddressData);
    //     Alert.alert('Address Saved', 'New sender address saved to your profile.');
    //     setSaveSenderAddress(false); // Reset switch
    //   }
    //   if (showNewDeliveryAddressForm && saveDeliveryAddress && deliveryAddress.name && deliveryAddress.street1 && deliveryAddress.postalCode && deliveryAddress.country) {
    //     console.log("[LabelPreviewPage] Attempting to save new delivery address:", deliveryAddress);
    //     const { id, ...newAddressData } = deliveryAddress; // Exclude ID for new address
    //     await addSavedAddress('delivery', newAddressData);
    //     Alert.alert('Address Saved', 'New delivery address saved to your profile.');
    //     setSaveDeliveryAddress(false); // Reset switch
    //   }
    // } catch (error) {
    //   console.error("[LabelPreviewPage] Error saving address during print:", error);
    //   Alert.alert('Save Error', 'Could not save one or more new addresses. Please try saving them via your profile settings.');
    // }

    setIsPrinting(true);
    
    if (Platform.OS === 'web') {
        // Web-specific print: Inject only label content and styles
        const originalContents = document.body.innerHTML;
        const originalTitle = document.title;
        let tempStyleElement: HTMLStyleElement | null = null;

        try {
            // Use DOMParser to safely parse the htmlContent
            const parser = new DOMParser();
            const printDoc = parser.parseFromString(htmlContent, 'text/html');
            
            // Extract styles
            const styleContent = printDoc.head.querySelector('style')?.textContent || '';
            // Extract label container div
            const labelContainerHTML = printDoc.body.querySelector('.label-container')?.outerHTML || '<p>Error: Label content not found.</p>';

            // Create and append temporary style to main document head
            tempStyleElement = document.createElement('style');
            tempStyleElement.textContent = styleContent;
            document.head.appendChild(tempStyleElement);

            // Set body content to only the label container
            document.body.innerHTML = labelContainerHTML;
            document.title = box?.name || 'Packing Label'; // Set a relevant title for print

            // Add a small delay before printing - sometimes helps render styles
            await new Promise(resolve => setTimeout(resolve, 100)); 

            window.print(); // Trigger browser print dialog

        } catch (error) {
            console.error("Error preparing content for printing:", error);
            Alert.alert('Error', 'Could not prepare content for printing.');
        } finally {
            // IMPORTANT: Restore original page content and title
            document.body.innerHTML = originalContents;
            document.title = originalTitle;
            // Remove temporary style element
            if (tempStyleElement && document.head.contains(tempStyleElement)) {
                document.head.removeChild(tempStyleElement);
            }
            setIsPrinting(false);
        }
    } else {
        // Native print using Expo Print
        try {
          await Print.printAsync({ html: htmlContent });
        } catch (error) {
          console.error("Error printing label (native):", error);
          Alert.alert('Error', 'Could not print the label.');
        } finally {
          setIsPrinting(false);
      }
    }
  };

  // Component to render address section (sender or delivery)
  const renderAddressSection = (
    type: 'sender' | 'delivery',
    title: string
  ) => {
    const address = type === 'sender' ? senderAddress : deliveryAddress;

    const handleOpenModal = () => {
      setEditingAddressType(type);
      setIsAddressModalVisible(true);
    };

    return (
      <View style={styles.previewSection} className="label-print-hide">
        <Text style={styles.sectionTitle}>{title}</Text>
        <View style={styles.addressDisplayContainer}>
          {address && address.name ? (
            <>
              <Text style={styles.addressText}><strong>Name:</strong> {address.name}</Text>
              {address.companyName && <Text style={styles.addressText}><strong>Company:</strong> {address.companyName}</Text>}
              <Text style={styles.addressText}><strong>Street:</strong> {address.street1}</Text>
              {address.street2 && <Text style={styles.addressText}>{address.street2}</Text>}
              <Text style={styles.addressText}><strong>City:</strong> {address.city}</Text>
              {address.region && <Text style={styles.addressText}><strong>Region:</strong> {address.region}</Text>}
              <Text style={styles.addressText}><strong>Postcode:</strong> {address.postalCode}</Text>
              <Text style={styles.addressText}><strong>Country:</strong> {address.country}</Text>
            </>
          ) : (
            <Text style={styles.addressTextItalic}>No {type} address set.</Text>
          )}
          </View>
        <TouchableOpacity style={styles.setAddressButton} onPress={handleOpenModal}>
          <Text style={styles.setAddressButtonText}>{address && address.name ? `Change ${type.charAt(0).toUpperCase() + type.slice(1)} Address` : `Set ${type.charAt(0).toUpperCase() + type.slice(1)} Address`}</Text>
                </TouchableOpacity>
      </View>
    );
  };

  if (packingLoading || showLoading || (!box && !packingError && !showId)) {
    return <View style={styles.centered}><ActivityIndicator size="large" /></View>;
  }

  if (packingError || showError) {
    const message = packingError?.message || showError?.message || 'An unknown error occurred';
    return <View style={styles.centered}><Text style={styles.errorText}>Error loading data: {message}</Text></View>;
  }

  if (!showId) {
     return <View style={styles.centered}><Text style={styles.errorText}>Missing required Show ID.</Text></View>;
  }

  if (!box) {
    return <View style={styles.centered}><Text style={styles.errorText}>Box not found for the given Show ID.</Text></View>;
  }

  if (!show) {
    return <View style={styles.centered}><Text style={styles.errorText}>Show details could not be loaded.</Text></View>;
  }

  const renderCheckbox = (label: string, value: boolean, onValueChange: (newValue: boolean) => void) => (
    <TouchableOpacity onPress={() => onValueChange(!value)} style={styles.checkboxContainer}>
      <View style={[styles.checkbox, value && styles.checkboxChecked]}>
        {value && <Text style={styles.checkboxCheckmark}>✓</Text>}
      </View>
      <Text style={styles.checkboxLabel}>{label}</Text>
    </TouchableOpacity>
  );

  return (
    <ScrollView style={styles.container} contentContainerStyle={styles.contentContainer}>
        {/* 2-Column Grid Layout */}
        <View className="grid grid-cols-1 md:grid-cols-2 gap-6 md:gap-8">
            
            {/* --- Column 1: Info & Controls --- */}
            <View className="space-y-6">
                {/* Title */}
                <Text style={styles.title}>Label Preview: {box.name}</Text>

                {/* Handling Info Section */}
                <View style={styles.previewSection} className="label-print-hide"> 
                    <Text style={styles.sectionTitle}>Handling Info</Text>
                    {hasFragileItems && (
                      <Text style={styles.handlingTextWarning}>* Handle with care! *</Text>
                    )}
                    <Text style={styles.label}>Optional Handling/Warning Note:</Text>
                    <View style={styles.noteInputContainer}> 
                    <TextInput
                      style={styles.input}
                      value={handlingNote}
                        onChangeText={handleHandlingNoteChange}
                        onBlur={handleHandlingNoteBlur}
                      placeholder="e.g., This way up, Keep dry"
                      placeholderTextColor="#888"
                      multiline
                    />
                      {isSavingNote && (
                        <ActivityIndicator size="small" color="#ccc" style={styles.savingIndicator} />
                      )}
                    </View>
                </View>
        
                {/* Include Symbols Section */}
                <View style={styles.previewSection} className="label-print-hide"> 
                    <Text style={styles.sectionTitle}>Include Symbols</Text>
                    {renderCheckbox('Fragile', includeFragile, handleFragileChange)}
                    {renderCheckbox('This Way Up (↑↑)', includeThisWayUp, handleThisWayUpChange)}
                    {renderCheckbox('Keep Dry (☂️)', includeKeepDry, handleKeepDryChange)}
                    {renderCheckbox('Includes Batteries (🔋)', includeBatteries, handleBatteriesChange)}
                </View>

                {/* --- Sender Address Section --- */}
                {renderAddressSection('sender', 'Sender Address')}

                {/* --- Delivery Address Section --- */}
                {renderAddressSection('delivery', 'Delivery Address')}

            </View>
            
            {/* --- Column 2: Live Preview --- */}
            <View className="mt-2 md:mt-10"> {/* Add margin top for spacing, more on md+ */}
                {/* Live preview section */}
                <View style={styles.previewSection} className="label-print-hide">
                  <Text style={styles.sectionTitle}>Live Preview</Text>
                  <View style={styles.webviewContainer} className="label-preview-area">
                    {htmlContent ? (
                      Platform.OS === 'web' ? (
                        <iframe
                          srcDoc={htmlContent}
                          style={{
                            borderWidth: 0,
                            width: '100%',
                            height: '100%',
                          }}
                        />
                      ) : (
                        <WebView
                          originWhitelist={['*']} 
                          source={{ html: htmlContent }}
                          style={styles.webview}
                          scrollEnabled={false} 
                        />
                      )
                    ) : isGeneratingPreview ? (
                      <ActivityIndicator size="large" color="#ccc" />
                    ) : (
                      <Text style={styles.errorText}>Could not generate preview.</Text>
                    )}
                  </View>
                </View>

                {/* Print Button */} 
                <TouchableOpacity 
                    style={[styles.button, styles.printButton]} 
                    onPress={handlePrint} 
                    disabled={isPrinting || !htmlContent}
                >
                    <Text style={styles.buttonText}>{isPrinting ? 'Processing...' : 'Print Label'}</Text>
                </TouchableOpacity>

            </View>

        </View>
        <AddressSelectionModal
            isVisible={isAddressModalVisible}
            onClose={() => {
                setIsAddressModalVisible(false);
                setEditingAddressType(null);
            }}
            onAddressSelect={(selectedAddress) => {
                if (editingAddressType === 'sender') {
                    setSenderAddress(selectedAddress);
                } else if (editingAddressType === 'delivery') {
                    setDeliveryAddress(selectedAddress);
                }
                setIsAddressModalVisible(false);
                setEditingAddressType(null);
            }}
        />
    </ScrollView>
  );
}

